// Utilit√°rio para obter descri√ß√£o da MP pelo c√≥digo
function getDescricaoMP(codigo) {
    const cod = Number(codigo);
    const mp = materiaPrimaDatabase.find(mp => Number(mp.codigo) === cod);
    return mp ? mp.descricao : String(codigo);
}

