    function setupTestConsole() {
        const runBtn = document.getElementById('test-console-run');
        const input = document.getElementById('test-console-input');
        const output = document.getElementById('test-console-output');
        
        if (!runBtn) return;
        
        runBtn.addEventListener('click', () => {
            const code = input?.value || '';
            if (!code.trim()) {
                addTestLog('‚ùå C√≥digo vazio!', 'error');
                return;
            }
            
            try {
                addTestLog(`‚ñ∂ Executando: ${code.substring(0, 50)}...`, 'info');
                const result = eval(code);
                addTestLog(`‚úì Resultado: ${JSON.stringify(result)}`, 'success');
            } catch (error) {
                addTestLog(`‚úó Erro: ${error.message}`, 'error');
            }
        });
    }

