    // Toggle collapse/expand sidebar (desktop only)
    function toggleSidebarCollapse() {
        if (!sidebar) return;
        
        const isCollapsed = sidebar.classList.contains('sidebar-collapsed');
        
        if (isCollapsed) {
            // Expandir
            sidebar.classList.remove('sidebar-collapsed');
            sidebar.classList.add('sidebar-expanded');
            localStorage.setItem('sidebar-state', 'expanded');
            console.log('[TRACE][toggleSidebarCollapse] Sidebar expandido');
        } else {
            // Retrair
            sidebar.classList.remove('sidebar-expanded');
            sidebar.classList.add('sidebar-collapsed');
            localStorage.setItem('sidebar-state', 'collapsed');
            console.log('[TRACE][toggleSidebarCollapse] Sidebar retra√≠do');
        }
        
        // Atualizar √≠cone do toggle
        if (sidebarToggleBtn) {
            lucide.createIcons();
        }
    }

    // Restaurar estado do sidebar ao carregar p√°gina
    function restoreSidebarState() {
        if (!sidebar) return;
        
        const savedState = localStorage.getItem('sidebar-state') || 'expanded';
        
        if (savedState === 'collapsed') {
            sidebar.classList.remove('sidebar-expanded');
            sidebar.classList.add('sidebar-collapsed');
            console.log('[TRACE][restoreSidebarState] Sidebar restaurado como retra√≠do');
        } else {
            sidebar.classList.add('sidebar-expanded');
            sidebar.classList.remove('sidebar-collapsed');
            console.log('[TRACE][restoreSidebarState] Sidebar restaurado como expandido');
        }
    }

