    function updateCurrentShift() {
        const now = new Date();
        const hour = now.getHours();
        let currentShift;
        
        if (hour >= 7 && hour < 15) {
            currentShift = 'T1';
        } else if (hour >= 15 && hour < 23) {
            currentShift = 'T2';
        } else {
            currentShift = 'T3';
        }
        
        if (currentShiftDisplay) {
            currentShiftDisplay.textContent = currentShift;
        }
    }
    
