    // Atualizar feedback em tempo real do input de peso
    function updateWeightFeedback() {
        const weightInput = document.getElementById('quick-losses-weight');
        const helper = document.getElementById('weight-feedback-helper');
        
        if (!helper || !weightInput.value) {
            if (helper) {
                helper.innerHTML = '';
                helper.className = 'mt-1 text-sm';
            }
            return;
        }
        
        const rawValue = weightInput.value;
        const parsed = parseNumberPtBR(rawValue);
        
        // Detectar se h√° caractere suspeito
        const hasComma = rawValue.includes(',');
        const hasDot = rawValue.includes('.');
        const multipleDots = (rawValue.match(/\./g) || []).length > 1;
        const multipleCommas = (rawValue.match(/,/g) || []).length > 1;
        
        let feedbackHTML = '';
        let feedbackClass = 'text-gray-600';
        
        // Obter peso da pe√ßa para convers√£o (usa cat√°logo como fallback)
        const pieceWeightGrams = getActivePieceWeightGrams();
        
        if (multipleCommas || multipleDots) {
            feedbackHTML = `‚ö†Ô∏è <strong>Aviso:</strong> M√∫ltiplos separadores detectados! Usando: <strong>${parsed.toFixed(3)} kg</strong>`;
            feedbackClass = 'text-yellow-700 bg-yellow-50 p-2 rounded border border-yellow-200';
        } else if (parsed > 0) {
            // Interpreta√ß√£o bem-sucedida - mostrar kg
            feedbackHTML = `‚úì Interpretado como: <strong>${parsed.toFixed(3)} kg</strong>`;
            
            // ‚úÖ NOVO: Calcular e mostrar quantidade de pe√ßas
            if (pieceWeightGrams > 0) {
                const weightGrams = parsed * 1000; // Converter kg para gramas
                const estimatedPieces = Math.floor(weightGrams / pieceWeightGrams);
                feedbackHTML += ` ‚âà <strong class="text-blue-600">${estimatedPieces.toLocaleString('pt-BR')} pe√ßas</strong>`;
            }
            
            feedbackClass = 'text-green-700';
        } else if (rawValue && parsed === 0) {
            // Entrada n√£o-vazia mas n√£o foi convertida
            feedbackHTML = `‚ùå <strong>Erro:</strong> N√£o foi poss√≠vel interpretar "${rawValue}" como n√∫mero`;
            feedbackClass = 'text-red-700 bg-red-50 p-2 rounded border border-red-200';
        }
        
        if (helper) {
            helper.innerHTML = feedbackHTML;
            helper.className = `mt-1 text-sm font-medium ${feedbackClass}`;
        }
    }
    
    // Validar e corrigir entrada de peso ao sair do campo
    function validateWeightInput() {
        const weightInput = document.getElementById('quick-losses-weight');
        if (!weightInput || !weightInput.value) return;
        
        const rawValue = weightInput.value;
        const parsed = parseNumberPtBR(rawValue);
        
        if (parsed === 0 && rawValue.trim()) {
            // Campo tem texto mas n√£o foi interpretado como n√∫mero
            const confirmation = window.confirm(
                `N√£o consegui interpretar "${rawValue}" como peso.\n\n` +
                `Use v√≠rgula (,) para decimais: 1,5 kg\n` +
                `Ou ponto (.): 1.5 kg\n\n` +
                `Deseja limpar o campo e tentar novamente?`
            );
            if (confirmation) {
                weightInput.value = '';
                updateWeightFeedback();
                weightInput.focus();
            }
        } else if (parsed > 100) {
            // Alerta se peso muito alto (pode ser digita√ß√£o errada de 1,5 como 15)
            const confirmation = window.confirm(
                `‚ö†Ô∏è Peso muito alto: ${parsed.toFixed(3)} kg\n\n` +
                `Confirma este peso ou quer corrigir?`
            );
            if (!confirmation) {
                weightInput.value = '';
                updateWeightFeedback();
                weightInput.focus();
            }
        }
    }
    
